import { createSlice } from "@reduxjs/toolkit";

export const userSlice = createSlice({
    name: "user",
    initialState: {
        user: {
            id: null,
            username: null,
            admin: null
        }
    },
    reducers: {
        setUser: (state, action) => {
            state.value = action.payload
        }
    },
});

export const { setUser } = userSlice.actions;

export default userSlice.reducer;
